mapper.select:
SELECT A.iproduct, A.title, A.stored_pic AS productStoredPic, A.request_pic AS productRequestPic, A.deposit, A.iuser, C.ipayment, C.istatus, C.ibuyer, C.price, C.rental_duration AS rentalDuration, C.rental_start_date AS rentalStartDate, C.rental_end_date AS rentalEndDate, C.created_at AS createdAt, C.istatus AS cancel, D.iuser AS targetIuser, D.nick AS targetNick, D.stored_pic AS userStoredPic, D.request_pic AS userRequestPic FROM t_product A JOIN t_product_payment B ON A.iproduct = B.iproduct LEFT JOIN t_payment C ON B.ipayment = C.ipayment LEFT JOIN t_user D ON A.iuser = D.iuser
where C.ibuyer = #{loginedIuser}
where A.iuser = #{loginedIuser}
ORDER BY B.ipayment DESC
