mapper.select:
select U.iuser, U.nick, U.stored_pic as storedPic, U.request_pic as requestPic, PA.ipayment, P.iproduct, PA.istatus, PA.rental_start_date as rentalStartDate, PA.rental_end_date as rentalEndDate, PA.rental_duration as rentalDuration, PA.price, P.deposit from t_payment PA join t_product_payment PP on PA.ipayment = PP.ipayment join t_product P on PP.iproduct = P.iproduct join t_user U on U.iuser =
P.iuser
PA.ibuyer
PA.istatus in (-3, -2, 0, 1, 2, 3)
and PA.ibuyer = #{iuser}
and P.iuser = #{iuser}
