mapper.select:
select U.iuser, U.nick, U.stored_pic as storedPic, PA.ipayment, P.iproduct, PA.istatus, PA.rental_start_date as rentalStartDate, PA.rental_end_date as rentalEndDate, PA.rental_duration as rentalDuration, PA.price, P.deposit, U.phone, PA.code
, U.phone, PA.code, PA.created_at as createdAt, if(PA.ibuyer = #{iuser}, 1, if(P.iuser = #{iuser}, 2, 0)) as role
from t_payment PA join t_product_payment PP on PA.ipayment = PP.ipayment
and PA.ipayment = #{ipayment}
join t_product P on PP.iproduct = P.iproduct join t_user U on
U.iuser = P.iuser
U.iuser = PA.ibuyer
if(P.iuser = #{iuser}, PA.ibuyer = U.iuser, P.iuser = U.iuser)
PA.istatus in (-4, -3, -2, 0, 1, 2, 3)
and PA.ibuyer = #{iuser}
and P.iuser = #{iuser}
and PA.ibuyer = #{iuser} or P.iuser = #{iuser}
limit #{page}, #{paymentPerPage}
