mapper.insert:
INSERT INTO t_review
set
ibuyer = #{iuser}
,ipayment = #{ipayment}
, contents = #{contents}
, rating = #{rating}

mapper.update:
update t_review
set
contents = #{contents}
,rating = #{rating}
where ireview = #{ireview}

mapper.select:
SELECT COUNT(ireview)
FROM t_review
WHERE ibuyer = #{iuser}
and ipayment = #{ipayment}

SELECT ipayment
FROM t_payment
WHERE ibuyer = #{iuser}

select ibuyer, ipayment
FROM t_review
WHERE ireview = 1;
