mapper.insert:
INSERT INTO t_review

    ibuyer = #{iuser}
    ,ipayment = #{ipayment}

    , contents = #{contents}


    , rating = #{rating}

mapper.update:
update t_review


        , contents = #{contents}


        , rating = #{rating}


where ireview = #{ireview}

mapper.select:
SELECT COUNT(ireview)
FROM t_review
WHERE ibuyer = #{iuser}
and ipayment = #{ipayment}

SELECT ipayment
FROM t_payment
WHERE ibuyer = #{iuser}
