mapper.insert:
INSERT INTO t_review
set
ibuyer = #{iuser}
,ipayment = #{ipayment}
, contents = #{contents}
, rating = #{rating}

mapper.update:
update t_review
set
contents = #{contents}
,rating = #{rating}
where ireview = #{ireview}

mapper.select:
SELECT istatus
FROM t_payment
WHERE ipayment = #{ipayment}

SELECT COUNT(ireview) FROM t_review WHERE
ibuyer = #{iuser} and
ipayment = #{ipayment}

select count(PA.ipayment) as isExists, if(PA.ibuyer = #{iuser}, 1, 0) as isBuyer
from t_product_payment PP
join t_product P on PP.iproduct = P.iproduct
join t_payment PA on PP.ipayment = PA.ipayment and PA.ipayment = #{ipayment}
where P.iuser = #{iuser} or PA.ibuyer = #{iuser}

select ibuyer, ipayment
FROM t_review
WHERE ireview = #{ireview}

mapper.update:
update t_user
set rating = #{rating}
where iuser = #{iuser}
