mapper.select:
select U.nick, U.userStoredPic, U.userRequestPic, P.iproduct, concat(S.sido, G.gungu, E.eupmyun, P.rest_addr) as addr, P.title, P.request_pic as prodMainRequestPic, P.request_pic as prodMainRequestPic, P.rental_price as rentalPrice, P.rental_start_date as rentalStartDate, P.rental_end_date as rentalEndDate, (select count(L.iproduct) from t_like L where L.iproduct = P.iproduct) prodLike, P.iuser, P.contents, P.deposit, P.buyDate, P,x, P,y from t_product P join t_user U on P.iuser = U.iuser join t_eupmyun E on P.iaddr = E.ieupmyun join t_gungu G on E.igungu = G.igungu join t_sido S on G.isido = S.isido
P.iproduct = #{iproduct} and U.iuser = #{iuser}
and P.icategory = #{icategory}

select ieupmyun from t_eupmyun where eupmyun in (

    #{eup}

)

select P.price, P.buy_date as buyDate, P.rental_start_date as rentalStartDate, P.rental_end_date as rentalEndDate
from t_product P
where P.iproduct = #{iproduct} and P.icategory = #{icategory}

mapper.update:
update t_product


        icategory = #{icategory}


        , iaddr = #{iaddr}


        , rest_addr = #{restAddr}


        , title = #{title}


        , contents = #{contents}


        , stored_pic = #{storedMainPic.storedPic}, request_pic = #{storedMainPic.requestPic}


        , price = #{price}


        , rental_price = #{rental_price}


        , deposit = #{deposit}


        , buy_date = #{buyDate}


        , rental_start_date = #{rentalStartDate}


        , rental_end_date = #{rentalEndDate}


        , x = #{x}


        , y = #{y}


where iproduct = #{iproduct} and iuser = #{iuser}
